$(document).ready(function () {
    $("#wrapperbestel").hide();

    $("a.showbestel").click(function () {
      $("#wrapperbestel").show();
      $("#wrappertekst").hide()
    })
  
    if ($("li").hasClass("pdf")) {
      $("li.readspeaker").show();
      $("p.caption").show()
    } else {
      $("li.readspeaker").hide();
      $("p.caption").hide()
    }
  
    for (var a = 1; a <= 4; a++) {
      if ($("#divDownload" + a + " > ul > li ").hasClass("pdf")) {
        $("#divDownload" + a + " li.readspeaker ").show();
        $("#divDownload" + a + " li.readspeaker").css({"margin-bottom": "0px"})
      } else {
        $("#divDownload" + a + " ul.bd-icons li.readspeaker").hide();
        $("#divDownload" + a + " li.readspeaker").css({"margin-bottom": "10px"})
      }
    }
  });
  